from django.apps.registry import apps
from django.urls import include, path
from two_factor.views import (
    BackupTokensView, DisableView, LoginView, ProfileView, QRGeneratorView,
    SetupCompleteView, SetupView,
)

# Define the main core URLs
core = [
    path(
        'account/login/',
        LoginView.as_view(),
        name='login',
    ),
    path(
        'account/two_factor/setup/',
        SetupView.as_view(),
        name='setup',
    ),
    path(
        'account/two_factor/qrcode/',
        QRGeneratorView.as_view(),
        name='qr',
    ),
    path(
        'account/two_factor/setup/complete/',
        SetupCompleteView.as_view(),
        name='setup_complete',
    ),
    path(
        'account/two_factor/backup/tokens/',
        BackupTokensView.as_view(),
        name='backup_tokens',
    ),
]

# Define the profile URLs
profile = [
    path(
        'account/two_factor/',
        ProfileView.as_view(),
        name='profile',
    ),
    path(
        'account/two_factor/disable/',
        DisableView.as_view(),
        name='disable',
    ),
]

# Initialize an empty list for plugin URLs
plugin_urlpatterns = []

# Add plugin URLs dynamically if available
for app_config in apps.get_app_configs():
    if app_config.name.startswith('two_factor.plugins.'):
        namespace = app_config.label if app_config.name != 'two_factor.plugins.phonenumber' else None
        try:
            plugin_urlpatterns.append(
                path(
                    f'account/two_factor/{app_config.url_prefix}/',
                    include(f'{app_config.name}.urls', namespace)
                ),
            )
        except AttributeError:
            pass  # If a plugin doesn't have URLs, it will be skipped

# Combine all URL patterns
urlpatterns = core + profile + plugin_urlpatterns
